parameters.functionCode:
// Configure service connections between ARR apps
const services = $input.all();

// Define inter-service configurations
const serviceConnections = [
  {
    from: 'prowlarr',
    to: 'radarr',
    endpoint: '/api/v3/indexer',
    method: 'POST',
    body: {
      enable: true,
      appProfileId: 1,
      priority: 25,
      name: 'Prowlarr - Radarr',
      fields: []
    }
  },
  {
    from: 'prowlarr',
    to: 'sonarr',
    endpoint: '/api/v3/indexer',
    method: 'POST',
    body: {
      enable: true,
      appProfileId: 1,
      priority: 25,
      name: 'Prowlarr - Sonarr',
      fields: []
    }
  },
  {
    from: 'radarr',
    to: 'bazarr',
    endpoint: '/api/radarr',
    method: 'POST',
    body: {
      ip: '127.0.0.1',
      port: 7878,
      base_url: '/radarr',
      use_ssl: false,
      apikey: '' // Will be filled dynamically
    }
  }
];

// Create connection configurations
const connections = [];

for (const connection of serviceConnections) {
  const fromService = services.find(s => s.json.service === connection.from);
  const toService = services.find(s => s.json.service === connection.to);

  if (fromService && toService) {
    connections.push({
      json: {
        fromService: connection.from,
        toService: connection.to,
        fromUrl: fromService.json.baseUrl,
        toUrl: toService.json.baseUrl,
        fromApiKey: fromService.json.apiKey,
        toApiKey: toService.json.apiKey,
        endpoint: connection.endpoint,
        method: connection.method,
        body: {
          ...connection.body,
          // Dynamically set API keys where needed
          ...(connection.body.apikey === '' && { apikey: fromService.json.apiKey })
        }
      }
    });
  }
}

return connections;

parameters.jsCode:
// Process configuration results
const results = $input.all();

const summary = {
  totalConnections: results.length,
  successful: 0,
  failed: 0,
  details: []
};

for (const result of results) {
  const connection = result.json;
  const success = result.status === 200 || result.status === 201;

  if (success) {
    summary.successful++;
  } else {
    summary.failed++;
  }

  summary.details.push({
    from: connection.fromService,
    to: connection.toService,
    status: result.status,
    success: success,
    response: result.body
  });
}

return [{ json: summary }];
